import React, { useState } from 'react'
import "./style.css";
import { IconButton } from '@mui/material';
import DeleteIcon from "@mui/icons-material/Delete";
import SendIcon from "@mui/icons-material/Send";
import MessageOthers from './MessageOthers';
import MessageSelf from './MessageSelf';
import { useSelector } from 'react-redux';

function ChatArea() {
  const dispatch = useDispatch()
  const lightTheme =  useSelector((state) => state.themeKey);
  const [conversations, setConversations] = useState([
    {
      name: "John Doe",
      lastMessage: "Hello!",
      timeStamp: "now",
    },
    {
      name: "Jane Smith",
      lastMessage: "How are you?",
      timeStamp: "10m ago",
    },
    {
      name: "Alice Johnson",
      lastMessage: "See you later!",
      timeStamp: "1h ago",
    },
  ]);

  var props = conversations[0];

  return (
    <div className={`chatarea-container ${lightTheme ? '' : 'dark'}`}>
      <div className={`chatarea-header ${lightTheme ? '' : 'dark'}`}>
        <p className='conversation-icon'>{props.name[0].toUpperCase()}</p>
        <div className='header-text'>
          <p className='conversation-name'>{props.name}</p>
          <p className='conversation-timeStamp'>{props.timeStamp}</p>
        </div>
        <IconButton>
          <DeleteIcon className={`icon ${lightTheme ? '' : 'dark'}`}/>
        </IconButton>
      </div>
      <div className={`chatarea-body ${lightTheme ? '' : 'dark'}`}>
        <MessageOthers />
        <MessageSelf />
      </div>
      <div className={`input-area ${lightTheme ? '' : 'dark'}`}>
        <input type="text" placeholder='Type a message' className='search-box'/>
        <IconButton>
          <SendIcon />
        </IconButton>
      </div>
    </div>
  );
}

export default ChatArea;
