import React from 'react'
import { useNavigate } from 'react-router-dom'
import { useSelector } from 'react-redux'

function ConversationItem({ props }) {
  const lightTheme =  useSelector((state) => state.themeKey);
  const navigate = useNavigate();

  return (
    <div className={`conversation-container ${lightTheme ? '' : 'dark'}`} onClick={() => navigate('chat')}>
        <p className='conversation-icon'>{props.name[0].toUpperCase()}</p>
        <p className='conversation-name'>{props.name}</p>
        <p className='conversation-lastMessage'>{props.lastMessage}</p>
        <p className={`conversation-timestamp ${lightTheme ? '' : 'dark'}`} style={{position: "absolute", right: "10px", bottom: "10px", fontSize: "0.75rem"}}>{props.timeStamp}</p>
    </div>
  )
}

export default ConversationItem
