import React, { useState } from 'react'
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import GroupAddIcon from '@mui/icons-material/GroupAdd';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import NightlightIcon from '@mui/icons-material/Nightlight';
import LightModeIcon from '@mui/icons-material/LightMode';
import SearchIcon from '@mui/icons-material/Search';
import { IconButton } from '@mui/material';
import "./style.css";
import ConversationItem from './ConversationItem';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { toggleTheme } from '../features/themeSlice'


function Sidebar() {

    const navigate = useNavigate();
    const dispatch = useDispatch()
    const lightTheme =  useSelector((state) => state.themeKey);
    // const [lightTheme,setLightTheme] = useState(true);
    const [conversations, setConversations] = useState([
        {
          name: "John Doe",
          lastMessage: "Hello!",
          timeStamp: "now",
        },
        {
          name: "Jane Smith",
          lastMessage: "How are you?",
          timeStamp: "10m ago",
        },
        {
          name: "Alice Johnson",
          lastMessage: "See you later!",
          timeStamp: "1h ago",
        },
      ]);
    

    return (
        <div className='sidebar-container'>
            <div className={`sidebar-header ${lightTheme ? '' : 'dark'}`}>
                <div>
                    <IconButton title='Profile'>
                        <AccountCircleIcon className={`icon ${lightTheme ? '' : 'dark'}`} />
                    </IconButton>
                </div>
                <div>
                    <IconButton onClick={() => { navigate('users') }} title='Users'>
                        <PersonAddIcon className={`icon ${lightTheme ? '' : 'dark'}`} />
                    </IconButton>
                    <IconButton onClick={() => { navigate('groups') }} title='Groups'>
                        <GroupAddIcon />
                    </IconButton>
                    <IconButton onClick={() => { navigate('create-groups') }} title='Create Groups'>
                        <AddCircleIcon />
                    </IconButton>
                    <IconButton onClick={() => {dispatch(toggleTheme())}} title='Theme'>
                        { lightTheme ? <NightlightIcon /> : <LightModeIcon /> }
                    </IconButton>
                </div>
            </div>
            <div className='sidebar-search'>
                <IconButton>
                    <SearchIcon />
                </IconButton>
                <input type="text" placeholder='Search' className='search-box' />
            </div>
            <div className='sidebar-conversations'>
                {conversations.map((conversation) => {
                    return <ConversationItem props={conversation} key={conversation.name} />
                })}
            </div>
        </div>
    )
}

export default Sidebar
