import { createSlice } from "@reduxjs/toolkit";

const initialState = true;

const themeSlice = createSlice({
    name: 'themeSlice',
    initialState,
    reducers: {
        toggleTheme: (state) => {
            return !state;
        }
    }
});

export const { toggleTheme } = themeSlice.actions;
export default themeSlice.reducer;
